import { ProvinceService } from './../../../../service/province.service';
import {
  Component,
  Input,
  OnChanges,
  OnInit,
  SimpleChanges,
} from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { NzUploadChangeParam } from 'ng-zorro-antd/upload';

@Component({
  selector: 'app-add-share',
  templateUrl: './add-share.component.html',
  styleUrls: ['./add-share.component.scss'],
})
export class AddShareComponent implements OnChanges, OnInit {
  showWeekNumber: boolean = false;
  @Input() data: any;
  majorList: any;
  base64: any;
  file: any;
  image1: any =
    'data:image/png;base64,';
  allProvice: any = [];
  allDistrict: any = [];
  allWard: any;
  form: FormGroup = new FormGroup({
    name: new FormControl('', Validators.required),
    studentId: new FormControl('', Validators.required),
    email: new FormControl('', Validators.required),
    cccd: new FormControl('', Validators.required),
    birthday: new FormControl('', Validators.required),
    phone: new FormControl('', Validators.required),
    gender: new FormControl('', Validators.required),
    province: new FormControl('', Validators.required),
    district: new FormControl('', Validators.required),
    ward: new FormControl('', Validators.required),
    imageBase64: new FormControl('', Validators.required),
    major: new FormControl('', Validators.required),
  });

  constructor(private provinceService: ProvinceService) {
    console.log(this.data);
  }
  ngOnInit(): void {
    this.getProvince();
    this.getMajor();
  }

  ngOnChanges(changes: SimpleChanges): void {
    console.log(this.data);
  }

  getProvince() {
    this.provinceService.getAll().subscribe((res) => {
      console.log(res);
      this.allProvice = res;
    });
  }

  getMajor() {
    this.majorList = [
      { name: 'Ứng dụng phần mềm', code: 'UD' },
      { name: 'Marketing', code: 'MKT' },
      { name: 'Ngôn ngữ Anh', code: 'E' },
      { name: 'Mobile', code: 'MB' },
    ];
  }

  handleChange({ file, fileList }: NzUploadChangeParam): void {
    const status = file.status;
    this.file = file;
    if (status !== 'uploading') {
      console.log(file, fileList);
    }
    if (status === 'done') {
      alert('Done');
      // this.msg.success(`${file.name} file uploaded successfully.`);
    } else if (status === 'error') {
      alert('Error');
      // this.msg.error(`${file.name} file upload failed.`);
    }
  }

  convertToBase64() {
    const reader = new FileReader();
    reader.onload = (e: any) => {
      this.base64 = e.target.result;
    };
  }

  getImage(e: any) {
    let file: any = e.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (e) => {
      this.image1 = reader.result;
    };
  }

  changeProvince() {
    this.allDistrict = [];
    this.allWard = [];
    let c = this.form.controls['province'].value;
    this.allDistrict = this.allProvice.find((e: any) => e.code == c).districts;
    this.form.controls['district'].setValue('');
    this.form.controls['ward'].setValue('');
  }
  changeDistrict() {
    this.allWard = [];
    let c = this.form.controls['district'].value;
    this.allWard = this.allDistrict.find((e: any) => e.code == c).wards;
    this.form.controls['ward'].setValue('');
  }
  changeWard() {}

  genData() {
    this.form.controls['studentId'].setValue('PH');
  }

  upload(e: any) {}
}
